    const [updated, setUpdated] = useState(false);

    useEffect(()=>{
        axios.get("http://localhost:8000/api/findpirates")
            .then(res=>setPirates(res.data))  
            .catch(err=>console.log("Alas, have ye seen the White Whale?", err))}, [updated])    

    return (
        <div>
