import React, { useEffect, useState } from 'react';
import { navigate, Link } from '@reach/router';

import axios from 'axios';

export default props => {
    const [name, setName] = useState("");
    const [url, setUrl] = useState("");
    const [chests, setChests] = useState("");
    const [phrase, setPhrase] = useState("");
    const [position, setPosition] = useState("");
    const [leg, setLeg] = useState(true);
    const [patch, setPatch] = useState(true);
    const [hand, setHand] = useState(true);
    const [updated, setUpdated] = useState(true);

    const onSubmitHandler = e => {
        e.preventDefault();
        console.log(patch, "Submit")
        axios.post('http://localhost:8000/api/pirate', {
            name,
            url,
            chests,
            phrase,
            position,
            leg,
            patch,
            hand
        })
        .then(res=> {
            console.log("Yup", res)
            // props.setUpdated(true);
            navigate('/', {state: {setUpdated: setUpdated}});
        })
        .catch(err2=>console.log("Nope", err2))

    }

    return (
        <>
        <Link to="/">
            <button >
                    <p>View the Crew!</p>
                </button>
        </Link>

        <form onSubmit={onSubmitHandler}>
            <p>
                <label>Pirate Name</label>
                <input type="text" onChange={e=>setName(e.target.value)}/>
            </p>
            <p>
                <label>Image URL</label>
                <input type="text" onChange={e=>setUrl(e.target.value)}/>
            </p>
            <p>
                <label>Number of chests</label>
                <input type="number" onChange={e=>setChests(e.target.value)}/>
            </p>
            <p>
                <label>Catch Phrase</label>
                <input type="text" onChange={e=>setPhrase(e.target.value)}/>
            </p>
            <p>
                <label>Position</label>
                <input type="text" onChange={e=>setPosition(e.target.value)}/>
            </p>
            <p>
                <label>PegLeg</label>
                <input type="checkbox" onChange={e=>setLeg(!leg)}/>
            </p>
            <p>
                <label>Eye Patch</label>
                <input type="checkbox" onChange={e=>setPatch(!patch)}/>
            </p>
            <p>
                <label>Hook for hand...</label>
                <input type="checkbox" checked = "checked" onChange={e=>setHand(!hand)}/>
            </p>1
            <input type="submit"/>
        </form>
        </>
    )
}
